var addInventory = require('./inventory');
var addDialog = require('./dialog');
var makeCommonObject = require('./commonobject');

var NPC = function(properties){

	makeCommonObject( this, properties);
	addInventory( this );
	addDialog( this );

	this.commandStates.NAME_KNOWN = 1024;

	this.getDescription = function(player){
		return this.hasState(player, this.commandStates.NAME_KNOWN ) ? this.name : this.description ;
	};
};

module.exports = NPC;